import React, { useEffect, useState } from "react"
import "../../../CSS/ChosenMarker.css"

const ChosenMarker = ({map, data, setChosenMarker}) => {
    const [info, setInfo] = useState(null)

    useEffect(()=>{
        const placesService = new window.google.maps.places.PlacesService(map);
        placesService.getDetails({
            placeId: data.place_id
        }, (results, status)=>{
            if (status==="OK"){
                console.log("results are", results)
                setInfo(results)
            } else{
                alert("error getting details of place clicked")
            }
        })
    }, [])

    const renderImage = () => {
        if (info){
            if (info.photos) return <img src={info.photos[0].getUrl()}/>
        }
    }

    return (
        <div className="chosenMarkerContainer" onClick={()=>setChosenMarker(null)}>
            <div className="chosenMarkerPopUp" onClick={(e)=>e.stopPropagation()}>
                {renderImage()}
                <h2>{data.name}</h2>
                <h4>{data.address}</h4>
            </div>
        </div>
    )
}

export default ChosenMarker
