import React, {useState} from "react"
import "../../CSS/Messages.css"

const Messages = ({setMessagesShowing}) => {
    const [isClosing, setIsClosing] = useState(false)
    const handleClose = () => {
        setIsClosing(true)
        setTimeout(()=>{
            setMessagesShowing(false)
        }, 1000)
    }

    const renderCSS = () => {
        if (isClosing) return "closing"
        else return null
    }

    return (
    <div className="messages">
        <i onClick={()=>{setMessagesShowing(false)}} className={`fas slidemessagesuparrow fa-2x fa-chevron-up ${renderCSS()}`}></i>
    </div>)
}
export default Messages
