import React, { useState, useRef, useCallback } from 'react'
import { GoogleMap, useLoadScript, Marker, InfoWindow} from '@react-google-maps/api';
import "../../CSS/BrowseArea.css"
import SearchSidebar from "./BrowseAreaFolder/SearchSidebar.js"
import mapStyles from "../../mapStyles"
import { determineWhichIcon, convertLng } from "../../helperfunctions"

const containerStyle = {
    left: '30vw',
  width: '70vw',
  height: '100vh'
};

const options = {
  styles: mapStyles,
  disableDefaultUI: true
}

const places = ['places']

const BrowseArea = ({setWhichDashboardPage}) => {
  const [whichCategory, setWhichCategory] = useState("All")
  const [mapResults, setMapResults] = useState([])
  const [chosenMarker, setChosenMarker] = useState()

  // const { isLoaded } = useJsApiLoader({
  //   id: 'google-map-script',
  //   googleMapsApiKey: process.env.REACT_APP_GOOGLE_KEY,
  // })

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_KEY,
    libraries: places,
  });

  const mapRef = useRef()
  const [map, setMap] = useState(null)

  const onLoad = useCallback(function callback(map) {
    navigator.geolocation.getCurrentPosition(position=>{
      mapRef.current = map;
      const bounds = new window.google.maps.LatLngBounds();
      map.fitBounds(bounds);
      map.panTo(
        {lat: position.coords.latitude,
          lng: position.coords.longitude}
        )
        map.setZoom(14)
        setMap(map)
      }, (err)=>{console.log(err)});
    }, [])

  const onUnmount = useCallback(function callback(map) {
    setMap(null)
  }, [])

  const relocate = () => {
    navigator.geolocation.getCurrentPosition(position=>{
      map.panTo(
        {lat: position.coords.latitude,
          lng: position.coords.longitude}
        )
        map.setZoom(14)
        setMap(map)
    }, err=>{console.log("error in locater button" + err)})
  }

  const callMarkers = () => {
    let icon = determineWhichIcon(whichCategory)
    if (mapResults){
      return mapResults.map((eachLocation, index) => {
        return (
          <Marker key={eachLocation.place_id}
          position = {{lat: eachLocation.geometry.location.lat, lng: eachLocation.geometry.location.lng}}
          onClick={()=>setChosenMarker(eachLocation)}
          icon = {{
            url: icon,
            scaledSize: new window.google.maps.Size(35, 35),
            origin: new window.google.maps.Point(0,0),
            anchor: new window.google.maps.Point(15, 15)
        }}
          />
        )
      })
    }
  }

  const searchCategoriesNearby = () => {
    let c = whichCategory.toLowerCase()
    let lat = map.getCenter().lat()
    let lng = convertLng(map.getCenter().lng())
    const service = new window.google.maps.places.PlacesService(map);
    let location = new window.google.maps.LatLng(lat, lng);
    let request = {
      location: location,
      radius: '1500',
      keyword: c
    }
    service.nearbySearch(request, (results, status)=>{
      if (status === "OK") setMapResults(results)
    });
  }

  console.log("map results are ", mapResults)
  return isLoaded ? (
      <GoogleMap
        mapContainerStyle={containerStyle}
        zoom={10}
        onLoad={onLoad}
        onUnmount={onUnmount}
        options={options}
      >
        <SearchSidebar searchCategoriesNearby={searchCategoriesNearby} whichCategory={whichCategory} setWhichCategory={setWhichCategory}/>
        <i className="fas locate fa-location-arrow" onClick = {()=>{relocate()}}></i>
        { /* Child components, such as markers, info windows, etc. */ }
        {isLoaded?callMarkers():null}
        <></>
      </GoogleMap>
  ) : <div className="loadingScreen"><i className="fas fa-spinner">{loadError}</i></div>
}

export default React.memo(BrowseArea)
